
import scala.collection.mutable.ListBuffer

@main def DayThirteen2023: Unit = {
  val initTime = System.currentTimeMillis
  println("Welcome to day thirteen of the Advent of Code 2023!")
  val mirrors = input.map(Mirror(_))
  val mirrorSummary = mirrors.map(_.reflectionSummary).sum
  println("The sum of all reflection summaries is " + mirrorSummary)
  val smudgedMirrorSummary = mirrors.map(_.smudgedReflectionSummary).sum
  println("The sum of all smudged reflection summaries is " + smudgedMirrorSummary)
  val elapsedTime = System.currentTimeMillis-initTime
  println("This took " + elapsedTime + " milliseconds")
}

class Mirror(val patternRows: List[String]) {

  val patternColumns: List[String] = patternRows.transpose.map(_.mkString)
  val possibleHorizontalReflections: List[Int] = findPossibleReflections(patternRows)
  val possibleVerticalReflections: List[Int] = findPossibleReflections(patternColumns)
  val possibleHorizontalSmudgedReflections: List[Int] = findPossibleSmudgedReflections(patternRows)
  val possibleVerticalSmudgedReflections: List[Int] = findPossibleSmudgedReflections(patternColumns)
  val reflectionSummary: Int = findReflectionSummary()
  val smudgedReflectionSummary: Int = findSmudgedReflectionSummary()

  private def findPossibleReflections(rowsOrColumns: List[String]): List[Int] = {
    var result: List[Int] = List()
    for (i <- 0 until (rowsOrColumns.length-1)) {
      if (rowsOrColumns(i) == rowsOrColumns(i+1)) {
        result = (i+1) :: result
      }
    }
    result
  }

  private def findPossibleSmudgedReflections(rowsOrColumns: List[String]): List[Int] = {
    var result: List[Int] = List()
    for (i <- 0 until (rowsOrColumns.length - 1)) {
      val string1 = rowsOrColumns(i)
      val string2 = rowsOrColumns(i + 1)
      val comparison = findDifference(string1, string2)
      if (comparison < 2) {
        result = (i + 1) :: result
      }
    }
    result
  }

  def findReflectionSummary(): Int = {
    var summary = 0
    val reflectionVertical = (possibleVerticalReflections.map(i => (isReflection(i, patternColumns))) zip possibleVerticalReflections).toMap
    val reflectionHorizontal = (possibleHorizontalReflections.map(i => (isReflection(i, patternRows))) zip possibleHorizontalReflections).toMap
    if (reflectionVertical.contains(true)) {
      summary = reflectionVertical(true)
    } else if (reflectionHorizontal.contains(true)) {
      summary = reflectionHorizontal(true)*100
    }
    summary
  }

  def findSmudgedReflectionSummary(): Int = {
    var summary = 0
    val reflectionVertical = (possibleVerticalSmudgedReflections.map(i => (isSmudgedReflection(i, patternColumns))) zip possibleVerticalSmudgedReflections).toMap
    val reflectionHorizontal = (possibleHorizontalSmudgedReflections.map(i => (isSmudgedReflection(i, patternRows))) zip possibleHorizontalSmudgedReflections).toMap
    if (reflectionVertical.contains(true)) {
      summary = reflectionVertical(true)
    } else if (reflectionHorizontal.contains(true)) {
      summary = reflectionHorizontal(true) * 100
    }
    summary
  }

  def isReflection(index: Int, rowsOrColumns: List[String]): Boolean = {
    val split = rowsOrColumns.splitAt(index)
    val splitStart = split._1.reverse
    val splitEnd = split._2
    (splitStart.startsWith(splitEnd)) || (splitEnd.startsWith(splitStart))
  }

  def isSmudgedReflection(index: Int, rowsOrColumns: List[String]): Boolean = {
    val split = rowsOrColumns.splitAt(index)
    val splitStart = split._1.reverse
    val splitEnd = split._2
    var difference = 0
    for (i <- 0 until (splitStart.length).min(splitEnd.length)) {
      difference += findDifference(splitStart(i), splitEnd(i))
    }
    difference == 1
  }

  def findDifference(string1: String, string2: String): Int = {
    string1.zip(string2).count {
      case (charA, charB) => charA != charB
    }
  }
}

